    public void ativarOngCbx(){
        try{
            OngDAO dao = new OngDAO();
            for(Ong ong : dao.buscarOngs()){
                ongComboBox.addItem(ong);
            }
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, "Falha ao buscar as ONGs");
        }
    }
    
    public void deletarOng(){
        OngDAO dao = new OngDAO();
        
        try{
            Ong ong = (Ong) ongComboBox.getSelectedItem();
            dao.removeOng(ong);
            JOptionPane.showMessageDialog(null, "ONG deletada com sucesso"); 
        }
        catch(Exception e){
           JOptionPane.showMessageDialog(null, "Falha ao deletar a ONG"); 
        }
    }

